select count(y."DOLocationID") as frequency,"DOLocationID", t2."Zone" from yellow_taxi_data y, taxi_zones t1, taxi_zones t2 where y."PULocationID"=t1."LocationID" and y."DOLocationID"=t2."LocationID" and extract (day from y."tpep_pickup_datetime")=14 and extract(month from y."tpep_pickup_datetime")=1 and t1."Zone"='Central Park' group by y."DOLocationID", t2."Zone" order by frequency desc limit 1 
